import React, { useEffect, useState } from "react";

const Dashboard = ({ token }) => {
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState("All");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/jobs", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        console.log("Jobs data from backend:", data);
        // âœ… Fix: Ensure jobs is always an array
        setJobs(Array.isArray(data) ? data : data.jobs || []);
      } catch (error) {
        console.error("Error fetching jobs:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchJobs();
  }, [token]);

  // âœ… Filter logic with safety
  const filteredJobs =
    Array.isArray(jobs) && filter !== "All"
      ? jobs.filter((job) => job.status === filter)
      : Array.isArray(jobs)
      ? jobs
      : [];

  // âœ… Summary counts
  const counts = {
    Applied: jobs.filter((j) => j.status === "Applied").length,
    Interview: jobs.filter((j) => j.status === "Interview").length,
    Offer: jobs.filter((j) => j.status === "Offer").length,
    Rejected: jobs.filter((j) => j.status === "Rejected").length,
  };

  if (loading) {
    return (
      <div style={{ textAlign: "center", marginTop: "100px", color: "#555" }}>
        <h3>Loading your jobs...</h3>
      </div>
    );
  }

  return (
    <div className="dashboard-container" style={styles.container}>
      <h1 style={styles.title}>ðŸ“Š Job Application Dashboard</h1>

      {/* Filter Bar */}
      <div style={styles.filterBar}>
        <label style={styles.label}>Filter by Status: </label>
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          style={styles.select}
        >
          <option>All</option>
          <option>Applied</option>
          <option>Interview</option>
          <option>Offer</option>
          <option>Rejected</option>
        </select>
      </div>

      {/* Job Status Summary */}
      <div style={styles.summary}>
        {Object.keys(counts).map((key) => (
          <div key={key} style={styles.summaryCard}>
            <h3>{key}</h3>
            <p>{counts[key]}</p>
          </div>
        ))}
      </div>

      {/* Job List */}
      <div style={styles.jobList}>
        {filteredJobs.length === 0 ? (
          <p style={{ color: "#777" }}>No jobs found for this filter.</p>
        ) : (
          filteredJobs.map((job) => (
            <div key={job._id} style={styles.jobCard}>
              <h2 style={{ color: "#007bff" }}>{job.title}</h2>
              <p>
                <strong>Company:</strong> {job.company}
              </p>
              <p>
                <strong>Status:</strong> {job.status}
              </p>
              <p>
                <strong>Notes:</strong> {job.notes || "â€”"}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// ðŸª„ Inline styles for a simple, HR-friendly look
const styles = {
  container: {
    padding: "30px",
    background: "#f9fbff",
    minHeight: "100vh",
    fontFamily: "Poppins, sans-serif",
  },
  title: {
    textAlign: "center",
    color: "#004aad",
    marginBottom: "20px",
  },
  filterBar: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "10px",
    marginBottom: "20px",
  },
  label: {
    fontWeight: "bold",
    color: "#333",
  },
  select: {
    padding: "5px 10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    backgroundColor: "#fff",
  },
  summary: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: "15px",
    marginBottom: "30px",
  },
  summaryCard: {
    background: "#e8f0fe",
    borderRadius: "10px",
    padding: "15px",
    textAlign: "center",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
  },
  jobList: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "20px",
  },
  jobCard: {
    background: "#fff",
    borderRadius: "10px",
    padding: "15px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    transition: "0.3s",
  },
};

export default Dashboard;
